values (1, 446, 'https://www.facebook.com/groups/ciovietnam/permalink/2486769021410219', 'Có thể đây là lý do mà các cty giao hàng phát triển cực kỳ nhanh ở Việt Nam chăng?', to_date('27-08-2019 13:05:00', 'dd-mm-yyyy hh24:mi:ss'), 23, 0, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (6, 696, 'https://www.facebook.com/groups/ciovietnam/permalink/2480220872065034', 'PRODUCT MINDSET- QUYẾT ĐỊNH SỐNG CÒN CỦA SẢN PHẨM CÔNG NGHỆProduct mindset nói một cách dễ hiểu là tư duy làm sản phẩm hướng đến người dùng. Để phát triển một sản phẩm CNTT, tư duy sản phẩm đóng vai trò cực kì quan trọng bởi chỉ có được một sản phẩm thật sự đáp ứng cho người dùng khi đội ngũ làm ra sản phẩm có tư duy phát triển sản phẩm vì người dùng. Tư duy sản phẩm không chỉ cần có ở Product Manager, BA mà thực tế tất cả các thành viên trong team phát triển sản phẩm cần có mindset này như CTO, Project Manager, Team lead mà còn Software Engineer, Designer, Tester… hay đến những thành viên không làm về kỹ thuật cũng cần có như Marketing, Branding, Sales. Khi đó cả một team mới có thể cùng nhau làm nên một sản phẩm thiết thực, hữu ích và am hiểu người dùng.Đặc biệt đối với một developer thì cần phải có tư duy sản phẩm. Khi developer có tư duy sản phẩm tốt, developer ấy sẽ luôn tìm cách đáp ứng được tốt nhất cho người dùng cả về mặt tính năng và trải nghiệm người dùng. Developercó tư duy sản phẩm luôn bắt đầu làm sản phẩm bằng một giải pháp tổng thể, sau đó mới nghĩ đến hướng giải quyết, cuối cùng là nghiên cứu tìm kiếm các kiến thức cần thiết để phát triển sản phẩm. Ngược lại một developer không có tư duy sản phẩm sẽ luôn bị gò bó về mặt sản phẩm về mặt sản phẩm bởi chính kiến thức mà developer có sẵn.Tư duy sản phẩm giúp cho các developer vận dụng tốt nhất những kiến thức đã học để kết hợp với nhau làm nên một sản phẩm hưu ích hướng tới người dùng. Khi đó các developer không chỉ hướng đến mục tiêu giải quyết bài toán nhỏ hay cụ thể mà còn hướng đến mục tiêu làm sản phẩm hoàn thiện để đáp ứng nhu cầu người dùng từ tính năng đến thói quen, trải nghiệm người dùng.Nếu như việc chuyển đổi số trong các doanh nghiệp hiện nay tập trung vào đáp ứng nhu cầu trải nghiệm đang thay đổi từng ngày của khách hàng thì tư duy sản phẩm là một phần không thể thiếu trong quá trình làm chu', to_date('23-08-2019 19:52:00', 'dd-mm-yyyy hh24:mi:ss'), 11, 1, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (7, 696, 'https://www.facebook.com/groups/ciovietnam/permalink/2480331698720618', '"Hẹn gặp mọi người tại chủ đề đầu tiên """Business Model Canvas""" của chương trình Digital Transformation in action vào ngày maiThời gian: 8h30-11h30 ngày 24/8/2019Địa điểm: Trung Nguyên Legend Cafe, 246A Nam Kì Khởi Nghĩa, quận 3, HCM.Đặc biệt, cơ hội để nghe chia sẻ từ 2 vị diễn giảMr. Phúc Huỳnh @President CIO VietnamMr. Trung Lê @Digital Architect*Hình thức tổ chức là workshop, trao đổi 2 chiều tập trung vào kiến thức, kinh nghiệm để mang lại nhiều giá trị cho thành viên tham dự với số lượng quy mô chỉ 20- 30 thành viên/ lầnLink đăng kí: https://ciovn.org/digital-transformation-in-action/"', to_date('23-08-2019 21:02:00', 'dd-mm-yyyy hh24:mi:ss'), 9, 0, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (8, 318, 'https://www.facebook.com/groups/ciovietnam/permalink/2479573148796473', '"ISO không cấp chứng chỉ cho các tổ chức, kiểu như: ISO 9001, ISO 27001, mà bản chấn là các tổ chức tuyên bố tuân thủ, và một số đơn vị trung gian, không liên quan gì ISO, tự đứng ra để chứng nhận cho việc tuân thủ ISO của tổ chức đó.
Vì thế, các cơ quan, tổ chức trong nước hoàn toàn có quyền chứng nhận cho các doanh nghiệp khác là tuân thủ ISO, hay gần với hiện nay hơn là cấp chứng nhận ISO....
https://www.iso.org/certification.html"', to_date('23-08-2019 10:55:00', 'dd-mm-yyyy hh24:mi:ss'), 4, 1, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (24, 622, 'https://www.facebook.com/groups/ciovietnam/permalink/2461209797299475', '- CHIA SẺ SỰ KIỆN - Email và SMS là các công cụ thông dụng được sử dụng bởi các doanh nghiệp để tương tác với khách hàng. Dù rằng đây là các công cụ không còn mới và có thể coi là chuẩn mực về nền tảng nhưng vẫn còn rất nhiều hiểu sai về chúng.* Thế nào là spam và không spam?* Có các loại Email và SMS khác nhau như thế nào?* Các nền tảng email thông dụng và nền tảng nào phù hợp cho doanh nghiệp?* Các chỉ số nào là quan trọng để đánh giá hiệu quả chiến dịch email?* Tại sao email lại vào inbox (hay tại sao lại không?) Có cách nào để tăng số lượng inbox?* Tăng chỉ số open rate, CTR, click rate cho toàn bộ chiến dịch?* SMS và các định dạng của nó* SMS vs OTT - xu hướng nào cho tương lai?* Sự trỗi dậy của chatbot và tác động của nó đến quá trình tương tác với khách hàng⭐️ Là một số ít các câu hỏi mà chương trình Engagement: Email, SMS & OTT mong muốn mang lại lời giải đáp cho người tham gia. Diễn giả:◾️ Tú Bùi - CMO @Ringer Asia◾️ Tony Dang - Head of Customer Service @EmaticsĐối tượng tham dự: sinh viên năm 3,4 muốn tìm hiểu về ngành, intern, junior marketers muốn hiểu về định hướng phát triển, người muốn chuyển ngành sang marketing❤️ Agenda: Bạn sẽ nhận được gì tại sự kiện:+ Khái Quát Về Email Marketing và Những Điều Bạn Chưa Biết+ Các Nền Tảng Gửi Email, Ứng Dụng và Tích Hợp+ SMS, OTT & Các Công Cụ Tương Tác Khác+ Q&A và Trao Đổi Nhập ngay code FSM5@EARLYBIRD để giảm 50.000đ/vé đến ngày 11/08/2019!====== ĐĂNG KÝ NGAY TẠI https://topdev.vn/page/full-stack-marketer-5-email-sms-ott/  ======THÔNG TIN CHUNG⏰ Thời gian: 18:30 (checkin) - 21:30 ngày 15/08/2019 Địa điểm: Dreamplex 195 Building - 195 Điện Biên Phủ, Phường 15, Bình Thạnh, Hồ Chí Minh Website:  https://topdev.vn/page/full-stack-marketer-5-email-sms-ott/======THÔNG TIN LIÊN HỆ Ms. Thanh | 097 443 9015 | Email: thanbk@uan.vn  Ms. Thoa | 038 5098 969 | Email: thoa.nguyen@applancer.net', to_date('12-08-2019 17:40:00', 'dd-mm-yyyy hh24:mi:ss'), 9, 0, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (25, 641, 'https://www.facebook.com/groups/ciovietnam/permalink/2459623630791425', '[DIGITAL TRANSFORMATION IN ACTION[Chuyển đổi số không còn là xu hướng mà còn là điều cần thiết đối với những doanh nghiệp hiện nay. Nhất là đối với những lĩnh vực như bán lẻ, ngân hàng, tài chính và logistic..Với mục tiêu nâng cao năng lực các cấp quản lý trong doanh nghiệp hiểu đúng để làm đúng chiến lược chuyển đổi số, cộng đồng CIO Vietnam ra mắt chương trình "Digital Transformation in Action"Chương trình được tổ chức trong vòng 5 tuần liên tiếp với 5 chủ đề khác nhau1) Business Model Canval: Mô hình kinh doanh không chỉ đơn thuần là xác định giá trị sản phẩm và phân khúc khách hàng mà còn là bản đồ dẫn lối thành công, định hướng toàn bộ hoạt động kinh doanh/ phát triển thị trường cho sản phẩm đó2) Digital Transformation Framework: Để chuyển đổi số thành công sẽ cần nắm bắt được framework, phương pháp tiếp cận và tổ chức tài nguyên một cách hiệu quả3) Product Mindset: Tư duy sản phẩm là yếu tố quan trọng nhất trong quá trình chuyển đổi số để phát triển sản phẩm giải quyết bài toán kinh doanh của doanh nghiệp. Tư duy sản phẩm đóng vai trò cốt lõi thành vì một sản phẩm thật sự đáp ứng cho người dùng, có tư duy làm sản phẩm vì người dùng.4) Design Thinking: phương pháp thiết kế cung cấp cách tiếp cận dựa trên giải pháp để giải quyết các vấn đề. Phương pháp này cực kỳ hữu ích trong xử lý các vấn đề phức tạp vốn mập mờ hoặc không xác định, bằng cách hiểu rõ các nhu cầu liên quan đến con người, bằng cách điều chỉnh vấn đề theo các phương thức lấy con người làm trung tâm5) OKR: Phương pháp hỗ trợ cộng tác và hỗ trợ thiết lập mục tiêu một cách linh hoạt, tăng sự tập trung, trách nhiệm, tính liên kết và tăng tốc cho doanh nghiệp. OKR đang dược nhiều doanh nghiệp lựa chọn để giúp các mục tiêu trở nên minh bạch và dễ dàng kết nối toàn bộ nhân viên trong quá trình thực hiện mục tiêu.Chương trình bắt đầu vào ngày 24/8/2019Thời gian: 8h30-11h30 ngày 24/8/2019Địa điểm: Trung Nguy', to_date('11-08-2019 19:11:00', 'dd-mm-yyyy hh24:mi:ss'), 20, 2, 0);

insert into ssm_posts (ID, OWNER, LINK, CONTENT, TIME, NUM_REACT, NUM_COMMENT, NUM_SHARE)
values (26, 696, 'https://www.facebook.com/groups/ciovietnam/permalink/2458388864248235', '- Cách nhận biết các ảnh hưởng trong hoạt động kinh doanh khi xuất hiện công nghệ mới ?- Thách thức cho các bộ phận trong công ty trước sự thay đổi, cạnh tranh và ứng dụng Design Thinking để thiết lập các mục tiêu nhằm thay đổi & các giải pháp áp dụng Digital Transformation ?- Xu hướng ứng dụng Digital Transformation làm thay đổi trong các lĩnh vực kinh doanh truyền thống như bán lẻ, vận tải, ngân hàng,... ?- Case study thành công và thất bại về chuyển đổi số của các doanh nghiệp tiêu biểu về Business Model Canvas.Những câu hỏi này sẽ được trả lời và trao đổi 2 chiều tại chủ đề đầu tiên "Business Model Canvas "của chuỗi workshop Digital Transformation in action, thân mời các anh chị tham khảo tại : https://ciovn.org/digital-transformation-in-action/?', to_date('11-08-2019 08:00:00', 'dd-mm-yyyy hh24:mi:ss'), 9, 0, 0);

